#' Clinical Significance
#'
#' 
#' @param data .
#' @param pre .
#' @param post .
#' @param groupingVar .
#' @param dysNorms .
#' @param dys_mean .
#' @param dys_sd .
#' @param func_mean .
#' @param func_sd .
#' @param valueOfR .
#' @param cutoffs .
#' @param higherBetter .
#' @param table .
#' @param barplot .
#' @param scatterplot .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$table} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$barplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$scatterplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$table$asDF}
#'
#' \code{as.data.frame(results$table)}
#'
#' @export
clinsig <- function(
    data,
    pre,
    post,
    groupingVar = NULL,
    dysNorms = "baselineValues",
    dys_mean = 0,
    dys_sd = 0,
    func_mean = 0,
    func_sd = 0,
    valueOfR = -1,
    cutoffs = "a",
    higherBetter = TRUE,
    table = TRUE,
    barplot = FALSE,
    scatterplot = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("clinsig requires jmvcore to be installed (restart may be required)")

    if ( ! missing(pre)) pre <- jmvcore::resolveQuo(jmvcore::enquo(pre))
    if ( ! missing(post)) post <- jmvcore::resolveQuo(jmvcore::enquo(post))
    if ( ! missing(groupingVar)) groupingVar <- jmvcore::resolveQuo(jmvcore::enquo(groupingVar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(pre), pre, NULL),
            `if`( ! missing(post), post, NULL),
            `if`( ! missing(groupingVar), groupingVar, NULL))


    options <- clinsigOptions$new(
        pre = pre,
        post = post,
        groupingVar = groupingVar,
        dysNorms = dysNorms,
        dys_mean = dys_mean,
        dys_sd = dys_sd,
        func_mean = func_mean,
        func_sd = func_sd,
        valueOfR = valueOfR,
        cutoffs = cutoffs,
        higherBetter = higherBetter,
        table = table,
        barplot = barplot,
        scatterplot = scatterplot)

    analysis <- clinsigClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

