#' Clinical Significance
#'
#' 
#' @param data .
#' @param pre .
#' @param post .
#' @param valueOfR .
#' @param alt .
#' @param cutoffs .
#' @param varEq .
#' @param higherBetter .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$dotplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
clinsig <- function(
    data,
    pre,
    post,
    valueOfR,
    alt = "notequal",
    cutoffs = "a",
    varEq = TRUE,
    higherBetter = TRUE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("clinsig requires jmvcore to be installed (restart may be required)")

    if ( ! missing(pre)) pre <- jmvcore::resolveQuo(jmvcore::enquo(pre))
    if ( ! missing(post)) post <- jmvcore::resolveQuo(jmvcore::enquo(post))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(pre), pre, NULL),
            `if`( ! missing(post), post, NULL))


    options <- clinsigOptions$new(
        pre = pre,
        post = post,
        valueOfR = valueOfR,
        alt = alt,
        cutoffs = cutoffs,
        varEq = varEq,
        higherBetter = higherBetter)

    analysis <- clinsigClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

